import {REMOVE_CONTACT} from './types'

export const removeContact =(data,id)=> dispatch=>{     
  let newData    =  data.map(a=>{
        if(a.id != id){return a}
  })
    dispatch({
              type:REMOVE_CONTACT,
              payload:{newData}
         })
}
